import React, { useState } from 'react';
import { Route } from 'react-router-dom';
import { Field, reduxForm } from 'redux-form';
import Logout from '../../content/login/logout';
import s from './headerLabel.module.css';

export default function HeaderLabel({ isAuth, currentUser, logout, history }) {
  return (
    <>
      {isAuth && currentUser && <UserLabel currentUser={currentUser} logout={logout} />}
      <Route exact path="/login" render={() => <LoginLabel isAuth={isAuth} history={history} />} />
      <Route
        exact
        path="/register"
        render={() => <RegisterLabel isAuth={isAuth} history={history} />}
      />
    </>
  );
}

const LoginLabel = ({ isAuth, history }) => {
  return (
    <div>
      {!isAuth && (
        <span tabIndex={0} role="link">
          login
        </span>
      )}
    </div>
  );
};

const RegisterLabel = ({ isAuth, history }) => {
  return (
    <div>
      {!isAuth && (
        <span tabIndex={0} role="link">
          Registration
        </span>
      )}
    </div>
  );
};

export const UserLabel = ({ currentUser, logout }) => {
  return (
    <>
      <div className="col">
        <SearchForm initialValues={{ searchField: '' }} s />
      </div>
      <div className="col">
        <span className={s.currentUser}> {`current user is ${currentUser.username} `}</span>
      </div>
      <div className="col">
        <span className={s.logout}>
          <Logout logout={logout} />
        </span>
      </div>
    </>
  );
};

const SearchForm = reduxForm({ form: 'searchTask' })(props => {
  return (
    <span>
      <Field
        className={`form-control ${s.searchForm}`}
        component="input"
        name="searchField"
        placeholder="searchTask"
      />
    </span>
  );
});
