import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import { withRouter, Redirect } from 'react-router-dom';
import { getTasks } from '../../../redux/navReducer';
import { login } from '../../../redux/authReducer';
import s from './login.module.css';

const Login = (props) => {
  if (props.isAuth) return <Redirect to="/" />;
  const onSubmit = ({ username, email, password }) => props.login((username = ''), email, password)
    .then(() => props.getTasks())
    .catch((err) => {
      console.log("LOGIN CATCH" + JSON.stringify(err.message));
      return <Redirect to="/login" />;
    });
  return (
    <div className={`${s.loginWrapper}`}>
      <LoginForm onSubmit={onSubmit} />
            Not register yet?
      <div tabIndex={0} role="button" onClick={() => props.history.push('/register')}>
                Register now!
      </div>
    </div>
  );
};

let LoginForm = (props) => {
  const { handleSubmit } = props;
  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="inputEmail">Email</label>
        <Field
          component="input"
          name="email"
          placeholder="email"
          className="form-control"
          id="inputEmail"
        />
      </div>
      <div className="form-group">
        <label htmlFor="inputPassword">Password</label>
        <Field
          component="input"
          placeholder="password"
          name="password"
          type="password"
          className="form-control"
          id="inputPassword"
        />
      </div>
      <button type="submit" className="btn btn-primary">
                Sign in
      </button>
    </form>
  );
};
LoginForm = reduxForm({ form: 'login' })(LoginForm);
const mapStateToProps = state => ({
  isAuth: state.authReducer.isAuth,
});

export default connect(
  mapStateToProps,
  { login, getTasks },
)(withRouter(Login));
